function Tarif(props) {
    return (
        <div>
            <section>
            <article>
                <h4 class='pricename'>{props.name}</h4>
                <p>руб <span style = {{ fontSize: '50px', justifyContent: 'center', alignItems: 'center'}}>{props.price}</span> мес</p>
                <h2 class="text">до {props.speed} Мбит/сек</h2>
                <h3 class='text2'>Объем включенного трафика не ограничен</h3>
            </article>
            </section>
        </div>

    );
}
export default Tarif;
