 import React, {useState} from "react";
 import "./tarif.css"

export const  Tarif = ({tarif}) => {
  const {name , price, speed, color } = tarif;
  const [isSelected, setIsSelected] = useState (false);
  const [value, setValue] = useState (name);
  
  function getValue (event) {
    const newValue = event.target.value;
    setValue(newValue);
  }
  
  return (
  <div className= {`tarif ${color}`}>
  {
    isSelected ? (
      <input type="text"
      onChange={getValue}
      value={value}
      onBlur={()=> setiSelected (false)}
      />
    ) : (
      <div className='name' onClick={() => setIsSelected(true)}>
        {name}
        </div>
    )}
    <div className='price'>{price}</div>
    <div className="text"> до {speed} Мбит/сек </div>
    <div className='text2'> Объем включенного трафика не ограничен </div>
    </div>
      );
}
